// new ES6 syntax called destructering assignment which is the same as : 
// var h1 = React.DOM.h1; 
// var div = React.DOM.div;
// var ul = React.DOM.ul;
// var li = React.DOM.li;
var { h1, div, ul, li } = React.DOM; 

var element = (
  div({},         // this === div (f) w/ h1 & ul arg &-> ul arg contains li args 
    h1({}, "Hello"),
    ul({}, 
      li({}, "Mark with C"), 
      li({}, "Marc with K")
    )
  )
);

// element2 uses JSX but does the same things as the above
var element2 = (
  <div>
    <h1>Hello from JSX</h1>
    <ul>
      <li>Jewels</li>
      <li>Julia</li>
    </ul>
  </div>
);

React.render(element, document.body);

// debugger keyword here lets you see app.js in dev tools and see what React does with above
debugger;

// you can also make composite components like this below (it is the same thing as element2)
var App = React.createClass({
  render () {
    return (
      <div> // you always have to wrap ui in a div element ยก
        <h1>Hello from JSX</h1>
        <ul>
          <li>Jewels</li>
          <li>Julia</li>
        </ul>
      </div>
    );
  }
});

// new ES6 features make it so you can't call App() directly & you have to make it into a 
// factory first -- becuase of a new Class component/method(?) in ES6
App = React.createFactory(App);

React.render(App(), document.body);

var React = require('react');

var input = <input type="checkbox" />
//same as : 
//React.DOM.input({type: "checkbox"})

var Gravatar = React.createClass({
  render () {
    return <span>{this.props.email}</span>;
  }
});

var element = <Gravatar email="giulz42@gmail.com" />

React.render(element, document.body);
